  `price` float DEFAULT NULL,
  `latitude` float DEFAULT NULL,
  `longitude` float DEFAULT NULL,
  `minimum_nights` int DEFAULT NULL,
  `maximum_nights` int DEFAULT NULL,
  `description` text,
  `occupancy` int DEFAULT NULL,
  `bedrooms` int DEFAULT NULL,
  `bathrooms` int DEFAULT NULL,
  `license` varchar(200) NOT NULL,
  `has_availability` tinyint(1) DEFAULT '0',
  `amenities` text,
  `host_id` int NOT NULL,
  `neighbourhood_id` int DEFAULT NULL,
  `property_type_name` varchar(255) DEFAULT NULL,
  `room_type_name` varchar(255) DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`listing_id`),
  UNIQUE KEY `unique_listing_name_host` (`name`,`host_id`),
